import React, { Component } from 'react';

export class DarkSky extends Component {

    state = {
        loading : true,
        currently : "rad"
    };

    //variables
    // w_obj = "";

    weatherFetcher = (latitude, longitude) => {
        fetch(`https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/41a1128648549ffc3dbd383b9f821df6/${latitude},${longitude}`)
            .then(result => result.json())
            .then(obj => {
                let WEATHER_OBJ = {
                    "currently": "",
                    "hourly": "",
                    "daily": ""
                }
                //let weatherArray = [];
                WEATHER_OBJ.currently = obj.currently.summary;
                WEATHER_OBJ.hourly = obj.hourly.summary;
                WEATHER_OBJ.daily = obj.daily.summary;

                //weatherArray.push(WEATHER_OBJ)
                console.log('HELLO FROM weatherFetcher')
                console.log(WEATHER_OBJ)

                this.setState( 
                        {loading : false,
                        currently : w_obj.currently
                        });
                // return (WEATHER_OBJ)
                /*
                console.log(obj.currently.summary)
                console.log(obj.hourly.summary)
                console.log(obj.daily.summary)
                */
            })

    }

    // async componentDidMount(){
    //     // let w_obj = await this.weatherFetcher(36.1627, 86.7816);
    //     // this.setState( 
    //     //     {loading : false,
    //     //     currently : w_obj.currently
    //     //     });
    // }



    render() {
        weatherFetcher(36.1627, 86.7816);
        console.log("w_obj = ", this.state.currently)

        return (
            <div>

                <p>Nashville is {this.state.currently}</p>
                {/* <p>Nashville is {w_obj ? w_obj.currently : "rad"}</p> */}
            </div>
        );
    }
}

export default DarkSky;
